import java.io.BufferedReader;
import java.io.IOException;
import java.nio.file.Files;
import java.nio.file.Path;
import java.nio.file.Paths;

/*
 * This Java source file was generated by the Gradle 'init' task.
 */
public class App {

    public static void main(String[] args) {
        Path gates = Paths.get("resources/gates.js");
        jsLinter(gates);
    }

    /*This function takes in a Path for a JavaScript file and then reads that file, searching for lines that should end in a semicolon but currently don't.
    * Each line that is missing a semicolon will be printed out in the format of "Line " + line + ": Missing a semicolon"
    */
    public static void jsLinter(Path file) {
        try(BufferedReader reader = Files.newBufferedReader(file)) {
            String currentLine = "";
            int line = 0;
            while (currentLine != null) {
                if (!currentLine.isEmpty() && !currentLine.endsWith("{") && !currentLine.endsWith("}") && !currentLine.contains("if") && !currentLine.contains("else") && !currentLine.endsWith(";") && !currentLine.startsWith("//")) {
                    System.out.println("Line " + line + ": Missing semicolon");
                }

                line++;
                currentLine = reader.readLine();
            }
        }
        catch(IOException e) {
            System.out.println(e);
        }
    }
}
